package bus.uigen.widgets;public class PanelSelector  {	public static final String COMMAND= ".create(";		static PanelFactory factory;	//static PanelFactory factory = new AWTPanelFactory();		public static void setPanelFactory (PanelFactory newVal) {		factory = newVal;	}		public static  VirtualContainer createPanel() {		  String widgetID = VirtualToolkit.getObjectID(VirtualContainer.class);		  VirtualContainer toReturn = execCreate(widgetID);		  if(VirtualToolkit.isDistributed()){			  String command = VirtualContainer.COMMAND_LABEL+ widgetID + COMMAND +")";			  VirtualToolkit.sendCommand(command);		  }		  		  return toReturn;	}		public static VirtualContainer execCreate(String widgetID){		  if(!VirtualToolkit.containObjID(widgetID)){			  VirtualContainer toReturn = factory.createPanel();			  if(widgetID != null){				  VirtualToolkit.associate(widgetID, toReturn);				  toReturn.setID(widgetID);			  }			  return toReturn;		  }else{			  return (VirtualContainer) VirtualToolkit.getObjectByID(widgetID);		  }		}
}