package bus.uigen.widgets;import bus.uigen.distributed.client.ActionEventForwarder;public class TextFieldSelector  {	public final static String COMMAND = ".create(";	static TextFieldFactory factory;	//static PanelFactory factory = new AWTPanelFactory();
	  public static void setTextFieldFactory (TextFieldFactory newVal) {
		factory = newVal;  }  public static  VirtualTextField createTextField(String text) {	  String widgetID = VirtualToolkit.getObjectID(VirtualTextField.class);	  VirtualTextField toReturn = execCreate(widgetID, text);	  if(VirtualToolkit.isDistributed()){		  String command = VirtualTextField.COMMAND_LABEL+ widgetID + COMMAND +text+")";		  VirtualToolkit.sendCommand(command);	  }	  	  return toReturn;
	    }    public static VirtualTextField execCreate(String widgetID, String text){	  if(!VirtualToolkit.containObjID(widgetID)){		  VirtualTextField toReturn = factory.createTextField(text);		  if(widgetID != null){			  VirtualToolkit.associate(widgetID, toReturn);			  toReturn.setID(widgetID);		  }		  if(VirtualToolkit.isDistributed()){			  toReturn.addActionListener(new ActionEventForwarder(toReturn));		  }		  return toReturn;	  }else{		  return (VirtualTextField) VirtualToolkit.getObjectByID(widgetID);	  }  }    public static  VirtualTextField createTextField() {	  String widgetID = VirtualToolkit.getObjectID(VirtualTextField.class);	  VirtualTextField toReturn = execCreate(widgetID);	  if(VirtualToolkit.isDistributed()){		  String command = VirtualTextField.COMMAND_LABEL+ widgetID + COMMAND +")";		  VirtualToolkit.sendCommand(command);	  }	  	  return toReturn;	    }    public static VirtualTextField execCreate(String widgetID){	  if(!VirtualToolkit.containObjID(widgetID)){		  VirtualTextField toReturn = factory.createTextField();		  if(widgetID != null){			  VirtualToolkit.associate(widgetID, toReturn);			  toReturn.setID(widgetID);		  }		  if(VirtualToolkit.isDistributed()){			  toReturn.addActionListener(new ActionEventForwarder(toReturn));		  }		  return toReturn;	  }else{		  return (VirtualTextField) VirtualToolkit.getObjectByID(widgetID);	  }  }  
}